Handles.ISI3 = axes('Parent',j0, ...
    'Units','pix', ...
    'Box','off', ...
    'CameraUpVector',[0 1 0], ...
    'CameraUpVectorMode','manual', ...
    'Clipping','off', ...
    'Color',[1 1 1], ...
    'NextPlot','add', ...
    'Units','normalized', ...
    'Position',[.1 .2 .8 .15], ...
    'Tag','isi3', ...
    'UserData',1, ...
    'XColor',[0 0 0], ...
    'XLimMode','manual', ...
    'YColor',[0 0 0], ...
    'YLimMode','manual', ...
    'ZColor',[0 0 0], ...
    'ZLimMode','manual', ...
    'XTickLabel',[],...
    'TickDir','out');

Handles.ISI4 = axes('Parent',j0, ...
    'Units','pix', ...
    'Box','off', ...
    'CameraUpVector',[0 1 0], ...
